custom_ip.endpoint:
[{trunk_name}-endpoint]
type=endpoint
transport=transport-udp
aors={trunk_name}-aor
disallow=all
allow={codecs}
from_user={from_user}
from_domain={sip_server}
context={context}
rewrite_contact=yes
rtp_symmetric=yes
force_rport=yes
direct_media=no

transport.template:
[transport-udp]
type=transport
protocol=udp
bind=0.0.0.0:{port}
external_signaling_address={external_ip}
external_media_address={external_ip}
local_net={local_net}
allow_reload=yes
tos=cs3
cos=3
